import { put, takeLatest, takeEvery } from "redux-saga/effects";
import axios from "axios";

function* fetchBatchItem() {
    console.log('getting specific batch:', u);
    try{
        // GET THE INFO FROM OUR SERVER
        const infoBatchResponse = yield axios.get(`/api/edit`);
        // THEN SEND TO REDUX
        console.log('saga response!', infoBatchResponse.data);
        // PUT IS DISPATCH
        yield put ({
            type: 'SET_ITEM',
            payload: infoBatchResponse.data
        });

    }catch (error) {
        console.log(error);
    }
}




function* updateBatchItem(action) {
  console.log("updating Batch:", action.payload);
  try {
    yield axios.put(`/edit/:id`, action.payload);
    yield put({
      type: "SET_ITEM",
      payload: action.payload.id
    });
  } catch (error) {
    console.log("Error in PUT", error);
  }
}

function* editSaga() {
  yield takeLatest("FETCH_BATCH_ITEM", fetchBatchItem);
  yield takeEvery("UPDATE", updateBatchItem);
}

export default editSaga;
