import React, { Component } from "react";
import { connect } from "react-redux";

//This is the Create Batch page and will have the user able to:
// Create a Batch
// Name a Batch
// Input temps and time
// Submit the data to the database and be alerted if they are
// Unsuccessful ADD to Database AND Successful ADD to the Database.

class CreateBatchPage extends Component {
  state = {
    beer_type: '',
    beer_name: '',
    batch_name: '',
    temp_hlt: '',
    mash_in_temp: '',
    mash_out_temp: '',
    time_boil: '',
    notes: '',
  };

  handlePost = event => {
    event.preventDefault();
    console.log("Submit Button has been Clicked");
    this.props.dispatch({
      type: "POST_INFO",
      payload: this.state
    });
  };

//   handleChangeBatch = event => {
//     this.setState({
//       beer_style: event.target.value,
//       beer_name: event.target.value,
//       batch_number: event.target.value,
//       hlt_temp: event.target.value,
//       mash_in_temp: event.target.value,
//       mash_out_temp: event.target.value,
//       boil_time: event.target.value,
//       notes: event.target.value
//     });
//   };

  handleChangeBeerType = event => {
    this.setState({
      beer_type: event.target.value
    });
  };

  handleChangeBeerName = event => {
    this.setState({
      beer_name: event.target.value
    });
  };

  handleChangeBatchNumber = event => {
    this.setState({
      batch_name: event.target.value
    });
  };

  handleChangeHotLiquor = event => {
    this.setState({
      temp_hlt: event.target.value
    });
  };

  handleChangeMashIn = event => {
    this.setState({
      mash_in_temp: event.target.value
    });
  };

  handleChangeMashOut = event => {
    this.setState({
      mash_out_temp: event.target.value
    });
  };

  handleChangeBoilTime = event => {
    this.setState({
      time_boil: event.target.value
    });
  };

  handleChangeNotes = event => {
    this.setState({
      notes: event.target.value
    });
  };

  componentDidMount() {
    this.props.dispatch({
      type: "FETCH_BATCH_INFO"
    });
  };

  handleDelete(id, user_id) {
    console.log(id);
    console.log(user_id);
    this.props.dispatch({
      type: "DELETE_USER_BATCH",
      payload: { id: id, user_id: user_id }
    });
  }

  render() {
    let batchTable = this.props.reduxStore.batchReducer.map((batch) => {
      return (
        <div>
          <tr>


            <td>
            {batch.beer_name}
          </td>
          <td>
            {batch.batch_number}
          </td>
          <td>
            {batch.hlt_temp}
          </td>
          <td>
            {batch.mash_in_temp}
          </td>
          <td>
            {batch.mash_out_temp}
          </td>
          <td>
            {batch.boil_time}
          </td>
          <td>
            {batch.notes}
          </td>
          </tr>
            <button onClick={() => this.handleDelete(batch.id, batch.user_id)}>
              Delete
            </button>

        </div>
      );
    });
    return (
      <div>
        <div>{JSON.stringify(this.props.reduxStore.batchReducer)}</div>
        {/* <table> */}
        <table>
          <tr>
            <th>id</th>
            <th>user id</th>
            <th>beer id</th>
            <th>batch name</th>
            <th>HLT TEMP</th>
            <th>MASH-IN TEMP</th>
            <th>MASH-OUT TEMP</th>
            <th>BOIL TIME</th>
            <th>NOTES</th>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.id}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.user_id}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.beer_id}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.batch_name}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.temp_hlt}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.mash_in_temp}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.mash_out_temp}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.time_boil}</td>
          </tr>
          <tr>
            <td>{this.props.reduxStore.batchReducer.map.notes}</td>
          </tr>
          <tr>
            <td>Eve</td>
            <td>Jackson</td>
            <td>94</td>
          </tr>
        </table>

        {/* <thead>
            <th>
              <tr>
                <td>{this.props.reduxStore.batchReducer.beer_type}</td>
                Beer Type: Green {this.props.reduxStore.batchReducer.beer_type}
              </tr>
              <tr>Beer Name: GREEN </tr>
              <tr>Batch Number: 05 </tr>
              <tr>Batch HLT Temp: 165</tr>
              <tr>Batch MASH-IN Temp: 160</tr>
              <tr>Batch MASH-OUT Temp: 155</tr>
              <tr>Batch BOIL Time:60 </tr>
              <tr>Batch Notes: 95 HOURS</tr>
            </tr>
          </thead> */}
        {/* <tbody>{batchTable}</tbody>
        </table> */}
        <form onSubmit={this.handlePost}>
          <input
            onChange={this.handleChangeBeerType}
            type="text"
            placeholder="Choose from the Type"
          />
          <input
            onChange={this.handleChangeBeerName}
            type="text"
            placeholder="Name the Beer / Batch"
          />
          <input
            onChange={this.handleChangeBatchNumber}
            type="text"
            placeholder="Give your batch a number"
          />
          <input
            onChange={this.handleChangeHotLiquor}
            type="text"
            placeholder="Please log the HLT Temp"
          />
          <input
            onChange={this.handleChangeMashIn}
            type="text"
            placeholder="Please log the Mash-In Temp"
          />
          <input
            onChange={this.handleChangeMashOut}
            type="text"
            placeholder="Please log the Mash-In Temp"
          />
          <input
            onChange={this.handleChangeBoilTime}
            type="text"
            placeholder="Please log the Boil Time"
          />
          <input
            onChange={this.handleChangeNotes}
            type="text"
            placeholder="Please log any notes"
          />
          <button type="submit">Submit to Brew Log</button>
        </form>
      </div>
    );
  }
};

const mapStateToProps = reduxStore => {
    return{
        reduxStore
    };
};

export default connect(mapStateToProps)(CreateBatchPage);
